import React from 'react';
import { styled } from '@mui/material';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu';
import { Avatar } from '@mui/material';
import Logo from '../../../src/images/logo192.png';
import { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { deleteProduct } from '../../features/productSelection/ProductSlice.js';
import { setStep } from '../../features/step/stepSlice.js';
import theme from '../../utils/theme';

const Offset = styled('div')(({ theme }) => theme.mixins.toolbar);

/*
      Our main navigation bar, strongly inspired by MUI documentation
*/
const NavBar = () => {
  const [auth, setAuth] = React.useState(true);
  const [anchorEl, setAnchorEl] = React.useState(null);
      const dispatch = useDispatch();


  const handleChange = (event) => {
    setAuth(event.target.checked);
  };

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleClick = () => {
      dispatch(deleteProduct());
      dispatch(setStep(0));
  }

  return (
      <Box sx={{ flexGrow: 1,
            zIndex: 5}} height="10vh">
            <AppBar position="fixed" sx={{
                  backgroundColor: "#FFF",
                  color: "#FF66C4",
                  justifyContent: 'center',
                  boxShadow: 2,
                  height: '10vh'
            }}>
                  <Toolbar height="100%" sx={{position: 'relative', overflow: 'hidden', justifyContent: 'center'}}>
                        <Fragment>
                              <IconButton
                                    edge="start"
                                    color="inherit"
                                    aria-label="menu"
                                    aria-controls="menu-appbar"
                                    aria-haspopup="true"
                                    sx={{ mr: 2, position: 'absolute', left: '30px' }}
                                    onClick={handleMenu}
                              >
                                    <MenuIcon sx={{fontSize: '1.4em'}} />
                              </IconButton>
                              <Menu
                                    id="menu-appbar"
                                    anchorEl={anchorEl}
                                    anchorOrigin={{
                                          vertical: 'top',
                                          horizontal: 'left',
                                    }}
                                    keepMounted
                                    transformOrigin={{
                                          vertical: 'top',
                                          horizontal: 'right',
                                    }}
                                    open={Boolean(anchorEl)}
                                    onClose={handleClose}
                              >
                                    <MenuItem onClick={handleClose}>Profile</MenuItem>
                                    <MenuItem onClick={handleClose}>My account</MenuItem>
                              </Menu>
                        </Fragment>
                        <Link to={'/'} onClick={handleClick}>
                              <IconButton sx={{
                                    justifyContent:'center'
                              }}>
                                    <Avatar alt="logo" src={Logo} variant="square"
                                    sx={{width: 46, height: 46, position: 'relative', top: 2}}/>
                              </IconButton>
                        </Link>
                  </Toolbar>
            </AppBar>
            <Offset />
      </Box>
  );
}

export default NavBar;
